import React from 'react';
import styled from 'styled-components';

const Line = styled.div`
  position: absolute;
  width: ${(props) => props.length || '100px'};
  height: 2px;
  background: #000;
  transform: rotate(${(props) => props.angle || '0deg'});
  transform-origin: left;
  top: ${(props) => props.y || '50%'};
  left: ${(props) => props.x || '50%'};
`;

function Edge({ edge }) {
  const x1 = Math.random() * 400;
  const y1 = Math.random() * 400;
  const x2 = Math.random() * 400;
  const y2 = Math.random() * 400;

  const length = Math.sqrt((x2 - x1) ** 2 + (y2 - y1) ** 2);
  const angle = Math.atan2(y2 - y1, x2 - x1) * (180 / Math.PI);

  return <Line length={`${length}px`} angle={`${angle}deg`} x={`${x1}px`} y={`${y1}px`} />;
}

export default Edge;
